* {
  box-sizing: border-box;
}

body {
  width: 97vw;
  height: 100%;
  background-color: #1c1c1c;
  color: #fff;
  font-family: "Montserrat Alternates", sans-serif;
  padding: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0 auto;
  
}

.background {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url('../img/fon3.png'); 
  background-size: cover;
  background-position: center;
  filter: blur(30px); 
  z-index: -1;
}

.container {
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  margin: 0 auto;
  text-align: center;
  display: flex;
  flex-direction: column;
}

.btn {
  background-color: #1c1c1c;
  border-radius: 12px;
  border: 2px solid #464545;
  width: 100%;
  height: 70px;
  font-size: 24px;
  font-weight: bold;
  color: rgb(246, 255, 95);
  margin-top: 5px;
  cursor: pointer;
}

.exchange-box {
  display: flex;
  justify-content: center;
  flex-direction: column;
  text-align: center;
  background-color: #1c1c1c;
  border: 2px solid #464545;
  padding: 10px;
  border-radius: 15px;
  width: 350px;
}

.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: flex-end;
  z-index: 1000;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.modal-overlay.show {
  opacity: 1;
  visibility: visible;
}

.modal-content {
  background: #333;
  border-radius: 15px;
  padding: 15px;
  transform: translateY(100%);
  transition: all 0.5s ease;
  opacity: 0;
  margin: 10px;
  position: relative;
}

.modal-content.show {
  transform: translateY(0);
  opacity: 1;
}

.form-group {
  position: relative;
  margin: 20px; 
}

input[type="text"],
input[type="password"],
input[type="number"] {
  font-family: "Montserrat Alternates", sans-serif;
  width: 100%;
  height: 55px;
  padding: 10px 15px 10px 40px;
  font-size: 16px;
  border: 2px solid #464545;
  border-radius: 12px;
  background-color: #1c1c1c;
  color: #fff;
  outline: none;
}

.input-wrapper {
  position: relative;
  display: flex;
  align-items: center; 
}

.error {
  color: #ff4c4c;
  font-size: 12px;
  margin-top: 5px;
  text-align: center;
}

h2{
  margin: 20px 0;
}

.close-btn{
  position: absolute;
  top: 5px;
  right: 5px;
  background-color: transparent;

}

